script:
frappe.ui.form.on('Item', {
	refresh(frm) {
		// your code here
	},
	validate(frm) {
		frappe.call({
            method: 'aqiq_sales_and_purchase_report.service.item_changes.item_before_insert',
            args: {
                'self':frm.doc.name
            },
            callback: function(r) {
                if (!r.exc) {
                    // code snippet
                    frm.clear_table('taxes');
                    for (var i = 0; i < r.message.length; i++) {
                        var new_sales_person = frm.add_child("taxes", {
                            "item_tax_template": r.message[i].item_tax_template,
                            "tax_category": r.message[i].tax_category,
                            "valid_from": r.message[i].valid_from,
                            "minimum_net_rate": r.message[i].minimum_net_rate,
                            "maximum_net_rate": r.message[i].maximum_net_rate,
                        });
                    }
                    frm.refresh_field('taxes');
                }
            }
        });

	},
})
